import React, { useRef, useState } from 'react'
import { BsCart2 } from "react-icons/bs";
import { HiOutlineBars3 } from "react-icons/hi2";
import { FiX } from "react-icons/fi";

function Navbar() {

    const [openMenu, setOpenMenu] = useState(false);
  const menuOptions = [
    {
      text: "Home",
     
    },
    {
      text: "About",
    
    },
    {
      text: "Testimonials",
    
    },
    {
      text: "Contact",
   
    },
    {
      text: "Cart",

    },
  ];

  const navRef = useRef();
  const showNavbar = () => {
    navRef.current.classList.toggle("responsive-nav");

  }

  return (
    <div>

    <header>
			<h3>LOGO</h3>
			<nav ref={navRef}>
				<a href="/#">Home</a>
				<a href="/#">My work</a>
				<a href="/#">Blog</a>
				<a href="/#">About me</a>
				<button
					className="nav-btn nav-close-btn"
					onClick={showNavbar}>
					<FaTimes />
				</button>
			</nav>
			<button
				className="nav-btn"
				onClick={showNavbar}>
				<FaBars />
			</button>
		</header>
        

    </div>
  )
}

export default Navbar
